mapper.select:
/* kr.go.bai.eip.faa.cav.dao.FAACAV012QMapper.selectLoadSttcList */
SELECT AUD_YR ,ROW_KND_CD ,VIEW_YN ,STTC_TIT1 ,STTC_COL1 ,STTC_COL2 ,STTC_COL3 ,STTC_COL4 ,STTC_COL5 ,STTC_COL6 ,STTC_COL7 ,STTC_COL8 ,STTC_COL9 FROM (SELECT T1.ENF_YR AS AUD_YR ,NVL(T1.ORG_DVSN_CD, 0) AS ROW_KND_CD ,CASE WHEN SUBSTR(ADD_MONTHS(#{proStadDt}, -1), 0, 4) - 1 > T1.ENF_YR THEN 'N' ELSE 'Y' END VIEW_YN ,CASE WHEN ORG_DVSN_CD IS NULL THEN ENF_YR || '년 합계' WHEN ORG_DVSN_CD ='1' THEN '국가기관' WHEN ORG_DVSN_CD ='2' THEN '지방자치단체' WHEN ORG_DVSN_CD ='3' THEN '공공기관' WHEN ORG_DVSN_CD ='4' THEN '기타단체' END AS STTC_TIT1 ,NVL(T1.TOT,0) AS STTC_COL1 ,NVL(T1.PA,0) AS STTC_COL2 ,NVL(T1.HAE,0) AS STTC_COL3 ,NVL(T1.GANG,0) AS STTC_COL4 ,NVL(T1.JUNG,0) AS STTC_COL5 ,NVL(T1.BU,0) AS STTC_COL6 ,NVL(T1.INSA,0) AS STTC_COL7 ,NVL(T1.BIWE,0) AS STTC_COL8 ,NVL(T1.CNT,0) AS STTC_COL9 FROM (SELECT SUBSTR(DETAIL.ENF_DT,0,4) AS ENF_YR ,CASE WHEN SUBSTR(MASTER.ORG_DVSN_CD, 1, 1) = '1' THEN '1' WHEN SUBSTR(MASTER.ORG_DVSN_CD, 1, 1) = '2' THEN '2' WHEN SUBSTR(MASTER.ORG_DVSN_CD, 1, 1) = '3' THEN '3' ELSE '4' END AS ORG_DVSN_CD ,SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '001' THEN 1 ELSE 0 END) + SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '002' THEN 1 ELSE 0 END) + SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '003' THEN 1 ELSE 0 END) + SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '004' THEN 1 ELSE 0 END) + SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '013' THEN 1 ELSE 0 END) + SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '005' THEN 1 ELSE 0 END) + SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '015' THEN 1 ELSE 0 END) AS TOT
,SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '001' THEN 1 ELSE 0 END) AS PA ,SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '002' THEN 1 ELSE 0 END) AS HAE ,SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '013' THEN 1 ELSE 0 END) AS GANG ,SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '003' THEN 1 ELSE 0 END) AS JUNG ,SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '004' THEN 1 ELSE 0 END) AS BU ,SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '005' THEN 1 ELSE 0 END) AS INSA ,SUM(CASE WHEN RELPSN.HND_RQ_KND_CD = '015' THEN 1 ELSE 0 END) AS BIWE ,COUNT(DISTINCT MASTER.AUD_YR || MASTER.AUD_NO || MASTER.DSP_RQ_SNO) AS CNT FROM TBBADEAR001M MASTER ,TBBADEAR002D DETAIL ,TBBADEAR007M RELPSN WHERE MASTER.AUD_YR = DETAIL.AUD_YR AND MASTER.AUD_NO = DETAIL.AUD_NO AND MASTER.DSP_RQ_SNO = DETAIL.DSP_RQ_SNO AND MASTER.AUD_YR = RELPSN.AUD_YR AND MASTER.AUD_NO = RELPSN.AUD_NO AND MASTER.DSP_RQ_SNO = RELPSN.DSP_RQ_SNO AND MASTER.DSP_RQ_KND_CD IN ('210','620','621','623') AND DETAIL.DTM_STA_CD != '0' AND TO_DATE(DETAIL.ENF_DT) BETWEEN TO_DATE(TO_CHAR(ADD_MONTHS(#{proStadDt}, -1), 'YYYY')||'-01-01') AND LAST_DAY(ADD_MONTHS(#{proStadDt}, -1)) GROUP BY ROLLUP(SUBSTR(DETAIL.ENF_DT,0,4) ,CASE WHEN SUBSTR(MASTER.ORG_DVSN_CD, 1, 1) = '1' THEN '1' WHEN SUBSTR(MASTER.ORG_DVSN_CD, 1, 1) = '2' THEN '2' WHEN SUBSTR(MASTER.ORG_DVSN_CD, 1, 1) = '3' THEN '3' ELSE '4' END) ) T1 WHERE ENF_YR IS NOT NULL ORDER BY ENF_YR DESC ,CASE WHEN ENF_YR IS NULL THEN 1 WHEN ORG_DVSN_CD IS NULL THEN 2 WHEN ORG_DVSN_CD ='1' THEN 3 WHEN ORG_DVSN_CD ='2' THEN 4 WHEN ORG_DVSN_CD ='3' THEN 5 WHEN ORG_DVSN_CD ='4' THEN 6 END) UNION ALL SELECT AUD_YR ,ROW_KND_CD ,VIEW_YN ,STTC_TIT1 ,STTC_COL1 ,STTC_COL2 ,STTC_COL3 ,STTC_COL4 ,STTC_COL5 ,STTC_COL6 ,STTC_COL7 ,STTC_COL8 ,STTC_COL9 FROM( SELECT T2.AUD_YR ,T2.ROW_KND_CD ,CASE WHEN SUBSTR(ADD_MONTHS(#{proStadDt}, -1),6,2) = '01' AND T2.AUD_YR = (SELECT TO_CHAR(MAX(T2.AUD_YR)-1) FROM TWFAACAV200D T2 WHERE T2.STTC_CLSF_CD = '012' AND T2.STTC_TYP_SEQ = (SELECT MAX(T3.STTC_TYP_SEQ) FROM TWFAACAV200M T3 WHERE T3.STTC_CLSF_CD = '012' AND T3.STTC_DTM_DY IS NOT NULL)) THEN 'N' WHEN T1.STTC_YR > T2.AUD_YR THEN 'N' ELSE 'Y' END VIEW_YN ,T2.STTC_TIT1 ,T2.STTC_COL1 ,T2.STTC_COL2 ,T2.STTC_COL3 ,T2.STTC_COL4 ,T2.STTC_COL5 ,T2.STTC_COL6 ,T2.STTC_COL7 ,T2.STTC_COL8 ,T2.STTC_COL9 FROM TWFAACAV200M T1 INNER JOIN TWFAACAV200D T2 ON ( T1.STTC_TYP_CD = T2.STTC_TYP_CD AND T1.STTC_CLSF_CD = T2.STTC_CLSF_CD AND T1.STTC_TYP_SEQ = T2.STTC_TYP_SEQ ) WHERE T1.STTC_CLSF_CD = '012' AND T2.STTC_TYP_SEQ = (SELECT MAX(T3.STTC_TYP_SEQ) FROM TWFAACAV200M T3 WHERE T3.STTC_CLSF_CD = '012' AND T3.STTC_DTM_DY IS NOT NULL) AND T2.AUD_YR BETWEEN SUBSTR(ADD_MONTHS(#{proStadDt}, -13),0,4) AND SUBSTR(ADD_MONTHS(#{proStadDt},-13),0,4) ORDER BY T2.STTC_DATA_SNO)

/* kr.go.bai.eip.aep.arp.dao.FAACAV012QMapper.selectAffComboList */ SELECT STTC_TYP_SEQ AS CD, STTC_YR || '년 ' || STTC_STR_MON || '월' || DECODE(STTC_END_MON, '01', '', ' ~ ' || STTC_END_MON || '월') AS CD_NM FROM TWFAACAV200M WHERE STTC_TYP_CD = '10' AND STTC_CLSF_CD = '012'
AND STTC_DTM_DY IS NOT NULL
ORDER BY STTC_TYP_SEQ DESC

