/* kr.go.bai.eip.faa.cav.dao.FAACAV012QMapper.updateMaster */

    UPDATE TWFAACAV200M
       SET RMK = #{rmk}
          ,ADM_OPIN = #{admOpin}
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}

/* kr.go.bai.eip.faa.cav.dao.FAACAV012QMapper.updateDetail */

    UPDATE TWFAACAV200D
       SET STTC_COL1 = #{sttcCol1}
          ,STTC_COL2 = #{sttcCol2}
          ,STTC_COL3 = #{sttcCol3}
          ,STTC_COL4 = #{sttcCol4}
          ,STTC_COL5 = #{sttcCol5}
          ,STTC_COL6 = #{sttcCol6}
          ,STTC_COL7 = #{sttcCol7}
          ,STTC_COL8 = #{sttcCol8}
          ,STTC_COL9 = #{sttcCol9}                  
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}
       AND STTC_DATA_SNO = #{sttcDataSno}

/* kr.go.bai.eip.faa.cav.dao.FAACAV012QMapper.updateMasterDtmDy */

    UPDATE TWFAACAV200M
       SET STTC_DTM_DY = SYSDATE
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}
