mapper.select:
/* kr.go.bai.eip.faa.cav.dao.FAACAV014QMapper.selectLoadSttcList */
SELECT ENF_YR AS AUD_YR ,T2.ENF_YR || '년' AS STTC_TIT1 ,ROUND(T2.AM_BUN + T2.AM_SI + T2.AM_1 + T2.AM_2 + T2.AM_3 + T2.AM_4 + T2.AM_5,0) AS STTC_COL1 ,ROUND(T2.AM_BUN + T2.AM_SI,0) AS STTC_COL2 ,ROUND(T2.AM_BUN,0) AS STTC_COL3 ,ROUND(T2.AM_SI,0) AS STTC_COL4 ,ROUND(T2.AM_1 + T2.AM_2 + T2.AM_3 + T2.AM_4 + T2.AM_5,0) AS STTC_COL5 ,ROUND(T2.AM_1,0) AS STTC_COL6 ,ROUND(T2.AM_2,0) AS STTC_COL7 ,ROUND(T2.AM_3,0) AS STTC_COL8 ,ROUND(T2.AM_4,0) AS STTC_COL9 ,ROUND(T2.AM_5,0) AS STTC_COL10 ,CASE WHEN SUBSTR(ADD_MONTHS(#{proStadDt}, -1), 0, 4) - 1 > ENF_YR THEN 'N' ELSE 'Y' END AS VIEW_YN FROM ( SELECT T1.ENF_YR AS ENF_YR ,SUM(CASE WHEN T1.DSP_RQ_KND_CD = '110' THEN T1.AM/1000000 ELSE 0 END) AS AM_BUN ,SUM(CASE WHEN T1.DSP_RQ_KND_CD IN ('310','320','390','391','395','396','625') THEN T1.AM/1000000 ELSE 0 END) AS AM_SI /* 통보(시정완료) 추가 - 2018.02.06 yyh */ ,SUM(T1.AM_1/1000000) AS AM_1 ,SUM(T1.AM_2/1000000) AS AM_2 ,SUM(T1.AM_3/1000000) AS AM_3 ,SUM(T1.AM_4/1000000) AS AM_4 ,SUM(T1.AM_5/1000000) AS AM_5 FROM (
SELECT SUBSTR(DETAIL.ENF_DT, 1, 4) AS ENF_YR ,MASTER.DSP_RQ_KND_CD AS DSP_RQ_KND_CD ,NVL(DETAIL.PNL_DRIN_PSVT_AM, 0) + NVL(DETAIL.RSR_AM , 0) + NVL(DETAIL.SPLP_AM, 0) AS AM /*추징회수보존금액 + 환급금액 + 추급금액*/ ,NVL(DETAIL.BGT_RDC_AM , 0) AS AM_1 /*예산절감*/ ,NVL(DETAIL.INC_ICR_AM , 0) AS AM_2 /*수입증대*/ ,NVL(DETAIL.PPL_BDN_RDT_AM , 0) AS AM_3 /*국민편익*/ ,NVL(DETAIL.FRTN_INCR_AM , 0) AS AM_4 /*재산증가*/ ,NVL(DETAIL.EXPN_RTNL_AM , 0) AS AM_5 /*지출합리화*/ FROM TBBADEAR001M MASTER, TBBADEAR002D DETAIL WHERE MASTER.AUD_YR = DETAIL.AUD_YR AND MASTER.AUD_NO = DETAIL.AUD_NO AND MASTER.DSP_RQ_SNO = DETAIL.DSP_RQ_SNO AND DETAIL.ENF_DT BETWEEN TO_DATE(TO_CHAR(ADD_MONTHS(#{proStadDt}, -1), 'YYYY')||'-01-01') AND LAST_DAY(ADD_MONTHS(#{proStadDt}, -1)) AND DETAIL.DTM_STA_CD != '0' ) T1 GROUP BY T1.ENF_YR ) T2 UNION ALL SELECT T2.AUD_YR ,T2.STTC_TIT1 ,T2.STTC_COL1 ,T2.STTC_COL2 ,T2.STTC_COL3 ,T2.STTC_COL4 ,T2.STTC_COL5 ,T2.STTC_COL6 ,T2.STTC_COL7 ,T2.STTC_COL8 ,T2.STTC_COL9 ,T2.STTC_COL10 ,CASE WHEN SUBSTR(ADD_MONTHS(#{proStadDt}, -1),6,2) = '01' AND T2.AUD_YR = (SELECT TO_CHAR(MAX(T2.AUD_YR)-1) FROM TWFAACAV200D T2 WHERE T2.STTC_CLSF_CD = '014' AND T2.STTC_TYP_SEQ = (SELECT MAX(T3.STTC_TYP_SEQ) FROM TWFAACAV200M T3 WHERE T3.STTC_CLSF_CD = '014' AND T3.STTC_DTM_DY IS NOT NULL)) THEN 'N' WHEN T1.STTC_YR - 4 > T2.AUD_YR THEN 'N' ELSE 'Y' END VIEW_YN FROM TWFAACAV200M T1 INNER JOIN TWFAACAV200D T2 ON ( T1.STTC_TYP_CD = T2.STTC_TYP_CD AND T1.STTC_CLSF_CD = T2.STTC_CLSF_CD AND T1.STTC_TYP_SEQ = T2.STTC_TYP_SEQ ) WHERE T1.STTC_CLSF_CD = '014' AND T2.STTC_TYP_SEQ = (SELECT MAX(T3.STTC_TYP_SEQ) FROM TWFAACAV200M T3 WHERE T3.STTC_CLSF_CD = '014' AND T3.STTC_DTM_DY IS NOT NULL) AND T2.AUD_YR BETWEEN SUBSTR(ADD_MONTHS(#{proStadDt}, -49),0,4) AND SUBSTR(ADD_MONTHS(#{proStadDt},-13),0,4)

/* kr.go.bai.eip.aep.arp.dao.FAACAV014QMapper.selectAffComboList */ SELECT STTC_TYP_SEQ AS CD, STTC_YR || '년 ' || STTC_STR_MON || '월' || DECODE(STTC_END_MON, '01', '', ' ~ ' || STTC_END_MON || '월') AS CD_NM FROM TWFAACAV200M WHERE STTC_TYP_CD = '10' AND STTC_CLSF_CD = '014'
AND STTC_DTM_DY IS NOT NULL
ORDER BY STTC_TYP_SEQ DESC

mapper.insert:
/* kr.go.bai.eip.faa.cav.dao.FAACAV014QMapper.updateMaster */

    UPDATE TWFAACAV200M
       SET RMK = #{rmk}
          ,ADM_OPIN = #{admOpin}
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}

/* kr.go.bai.eip.faa.cav.dao.FAACAV014QMapper.updateDetail */

    UPDATE TWFAACAV200D
       SET STTC_COL1 = #{sttcCol1}
          ,STTC_COL2 = #{sttcCol2}
          ,STTC_COL3 = #{sttcCol3}
          ,STTC_COL4 = #{sttcCol4}
          ,STTC_COL5 = #{sttcCol5}
          ,STTC_COL6 = #{sttcCol6}
          ,STTC_COL7 = #{sttcCol7}
          ,STTC_COL8 = #{sttcCol8}
          ,STTC_COL9 = #{sttcCol9}
          ,STTC_COL10 = #{sttcCol10}
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}
       AND STTC_DATA_SNO = #{sttcDataSno}

/* kr.go.bai.eip.faa.cav.dao.FAACAV014QMapper.updateMasterDtmDy */

    UPDATE TWFAACAV200M
       SET STTC_DTM_DY = SYSDATE
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}
