mapper.select:
/* kr.go.bai.eip.aep.arp.dao.FAACAV019QMapper.selectAffComboList */ SELECT STTC_TYP_SEQ AS CD, STTC_YR || '년 ' || STTC_STR_MON || '월' || DECODE(STTC_END_MON, '01', '', ' ~ ' || STTC_END_MON || '월') AS CD_NM FROM TWFAACAV200M WHERE STTC_TYP_CD = '10' AND STTC_CLSF_CD = '019'
AND STTC_DTM_DY IS NOT NULL
ORDER BY STTC_TYP_SEQ DESC

mapper.insert:
/* kr.go.bai.eip.faa.cav.dao.FAACAV019QMapper.insertDetail */

    INSERT INTO TWFAACAV200D (
         STTC_TYP_CD
        ,STTC_CLSF_CD
        ,STTC_TYP_SEQ
        ,STTC_DATA_SNO
        ,STTC_TIT1
        ,STTC_TIT2
        ,STTC_COL1
        ,STTC_COL2
        ,STTC_COL3
        ,STTC_COL4
        ,STTC_COL5
        ,STTC_COL6
        ,STTC_COL7
        ,STTC_COL8
        ,STTC_COL9
        ,STTC_COL10
        ,STTC_COL11
        ,STTC_COL12
        ,AUD_YR
        ,ROW_KND_CD
        ,FRT_USR_ID
        ,FNL_USR_ID
        ,FNL_DEAL_DPT_CD
        ,FNL_MNU_ID
        ,FRT_REGI_DH
        ,FNL_MDF_DH )
    VALUES (
         '10'
        ,'019'
        ,( SELECT NVL(MAX(STTC_TYP_SEQ), 0)
             FROM TWFAACAV200M
            WHERE STTC_TYP_CD = '10'
              AND STTC_CLSF_CD = '019' )
        ,( SELECT NVL(MAX(STTC_DATA_SNO), 0) + 1
             FROM TWFAACAV200D
            WHERE STTC_TYP_CD = '10'
              AND STTC_CLSF_CD = '019' 
              AND STTC_TYP_SEQ = ( SELECT NVL(MAX(STTC_TYP_SEQ), 0)
                                     FROM TWFAACAV200M
                                    WHERE STTC_TYP_CD = '10'
                                      AND STTC_CLSF_CD = '019' ))
        ,#{sttcTit1}
        ,#{sttcTit2}
        ,#{sttcCol1}
        ,#{sttcCol2}
        ,#{sttcCol3}
        ,#{sttcCol4}
        ,#{sttcCol5}
        ,#{sttcCol6}
        ,#{sttcCol7}
        ,#{sttcCol8}
        ,#{sttcCol9}
        ,#{sttcCol10}
        ,#{sttcCol11}
        ,#{sttcCol12}                
        ,#{audYr}
        ,#{rowKndCd}
        ,#{usrId}
        ,#{usrId}
        ,#{dptCd}
        ,#{mnuId}
        ,SYSDATE
        ,SYSDATE )

/* kr.go.bai.eip.faa.cav.dao.FAACAV019QMapper.updateMaster */

    UPDATE TWFAACAV200M
       SET RMK = #{rmk}
          ,ADM_OPIN = #{admOpin}
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}

/* kr.go.bai.eip.faa.cav.dao.FAACAV019QMapper.updateDetail */

    UPDATE TWFAACAV200D
       SET STTC_COL1 = #{sttcCol1}
          ,STTC_COL2 = #{sttcCol2}
          ,STTC_COL3 = #{sttcCol3}
          ,STTC_COL4 = #{sttcCol4}
          ,STTC_COL5 = #{sttcCol5}
          ,STTC_COL6 = #{sttcCol6}
          ,STTC_COL7 = #{sttcCol7}
          ,STTC_COL8 = #{sttcCol8}
          ,STTC_COL9 = #{sttcCol9}
          ,STTC_COL10 = #{sttcCol10}
          ,STTC_COL11 = #{sttcCol11}
          ,STTC_COL12 = #{sttcCol12}
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}
       AND STTC_DATA_SNO = #{sttcDataSno}

/* kr.go.bai.eip.faa.cav.dao.FAACAV019QMapper.updateMasterDtmDy */

    UPDATE TWFAACAV200M
       SET STTC_DTM_DY = SYSDATE
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}
