mapper.select:
/* kr.go.bai.eip.faa.cav.dao.FAACAV021QMapper.selectLoadSttcList1 */
SELECT SUBSTR(M1.STTC_DT, 0, 4) AS AUD_YR ,SUBSTR(M1.STTC_DT, 0, 4) || '년' AS STTC_TIT1 , NVL(SUM(M1.STTC_CNT), 0) AS STTC_COL1 , NVL(SUM( CASE WHEN M1.TERM_DT BETWEEN 0 AND 90 THEN NVL(M1.STTC_CNT, 1) ELSE 0 END ), 0) AS STTC_COL2 , NVL(SUM( CASE WHEN M1.TERM_DT > 90 THEN NVL(M1.STTC_CNT, 1) ELSE 0 END ), 0) AS STTC_COL3 , NVL(SUM( CASE WHEN M1.TERM_DT BETWEEN 91 AND 180 THEN NVL(M1.STTC_CNT, 1) ELSE 0 END ), 0) AS STTC_COL4 , NVL(SUM( CASE WHEN M1.TERM_DT BETWEEN 181 AND 270 THEN NVL(M1.STTC_CNT, 1) ELSE 0 END ), 0) AS STTC_COL5 , NVL(SUM( CASE WHEN M1.TERM_DT BETWEEN 271 AND 365 THEN NVL(M1.STTC_CNT, 1) ELSE 0 END ), 0) AS STTC_COL6 , NVL(SUM( CASE WHEN M1.TERM_DT > 365 THEN NVL(M1.STTC_CNT, 1) ELSE 0 END ), 0) AS STTC_COL7 , NVL(TRUNC(SUM(M1.TERM_DT * M1.STTC_CNT)/SUM(M1.STTC_CNT), 0), 0) AS STTC_COL8 , CASE WHEN SUBSTR(ADD_MONTHS(#{proStadDt}, -1), 0, 4) - 1 > SUBSTR(M1.STTC_DT, 0, 4) THEN 'N' ELSE 'Y' END VIEW_YN FROM ( SELECT SUBSTR(NVL(D2.SBCN_DT, D3.ENF_DT),0,4) AS STTC_DT , TO_DATE(NVL(D2.SBCN_DT, D3.ENF_DT)) - TO_DATE(NVL(D3.RLS_DT, D1.ACP_DT)) AS TERM_DT , D1.STTC_CNT AS STTC_CNT FROM TBBADFRE012M D1 , TBBADFRE012D D2 , TBBADFRE013D D3 WHERE 1 = 1 AND D1.ACP_YR = D2.ACP_YR AND D1.ACP_DVSN_CD = D2.ACP_DVSN_CD AND D1.ACP_SRNO = D2.ACP_SRNO AND D1.DPT_CD = D2.DPT_CD AND D1.ACP_YR = D3.ACP_YR AND D1.ACP_DVSN_CD = D3.ACP_DVSN_CD AND D1.ACP_SRNO = D3.ACP_SRNO AND D1.DPT_CD = D3.DPT_CD AND D1.ACP_DVSN_CD = '1' AND NVL(NVL(D2.SBCN_DT, D3.ENF_DT), '99991231')BETWEEN TO_DATE(TO_CHAR(ADD_MONTHS(#{proStadDt}, -1), 'YYYY')||'-01-01') AND LAST_DAY(ADD_MONTHS(#{proStadDt}, -1)) ) M1 GROUP BY SUBSTR(M1.STTC_DT, 0, 4) UNION ALL SELECT TO_CHAR(T2.AUD_YR) AS AUD_YR ,STTC_TIT1 ,NVL(STTC_COL1,0) AS STTC_COL1 ,NVL(STTC_COL2,0) AS STTC_COL2 ,NVL(STTC_COL3,0) AS STTC_COL3 ,NVL(STTC_COL4,0) AS STTC_COL4 ,NVL(STTC_COL5,0) AS STTC_COL5 ,NVL(STTC_COL6,0) AS STTC_COL6 ,NVL(STTC_COL7,0) AS STTC_COL7 ,NVL(STTC_COL8,0) AS STTC_COL8 ,CASE WHEN SUBSTR(ADD_MONTHS(#{proStadDt}, -1),6,2) = '01' AND T2.AUD_YR = (SELECT TO_CHAR(MAX(T2.AUD_YR)-1) FROM TWFAACAV200D T2 WHERE T2.STTC_CLSF_CD = '021' AND T2.STTC_TYP_SEQ = (SELECT MAX(T3.STTC_TYP_SEQ) FROM TWFAACAV200M T3 WHERE T3.STTC_CLSF_CD = '021' AND T3.STTC_DTM_DY IS NOT NULL)) THEN 'N' WHEN T1.STTC_YR > T2.AUD_YR THEN 'N' ELSE 'Y' END VIEW_YN FROM TWFAACAV200M T1 INNER JOIN TWFAACAV200D T2 ON ( T1.STTC_TYP_CD = T2.STTC_TYP_CD AND T1.STTC_CLSF_CD = T2.STTC_CLSF_CD AND T1.STTC_TYP_SEQ = T2.STTC_TYP_SEQ ) WHERE T1.STTC_CLSF_CD = '021' AND T2.ROW_KND_CD = '1' AND T2.STTC_TYP_SEQ = (SELECT MAX(T3.STTC_TYP_SEQ) FROM TWFAACAV200M T3 WHERE T3.STTC_CLSF_CD = '021' AND T3.STTC_DTM_DY IS NOT NULL) AND T2.AUD_YR BETWEEN SUBSTR(ADD_MONTHS(#{proStadDt}, -49),0,4) AND SUBSTR(ADD_MONTHS(#{proStadDt},-13),0,4)
ORDER BY AUD_YR DESC

/* kr.go.bai.eip.aep.arp.dao.FAACAV021QMapper.selectAffComboList */ SELECT STTC_TYP_SEQ AS CD, STTC_YR || '년 ' || STTC_STR_MON || '월' || DECODE(STTC_END_MON, '01', '', ' ~ ' || STTC_END_MON || '월') AS CD_NM FROM TWFAACAV200M WHERE STTC_TYP_CD = '10' AND STTC_CLSF_CD = '021'
AND STTC_DTM_DY IS NOT NULL
ORDER BY STTC_TYP_SEQ DESC

mapper.insert:
/* kr.go.bai.eip.faa.cav.dao.FAACAV021QMapper.updateMaster */

    UPDATE TWFAACAV200M
       SET RMK = #{rmk}
          ,ADM_OPIN = #{admOpin}
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}

/* kr.go.bai.eip.faa.cav.dao.FAACAV021QMapper.updateDetail */

    UPDATE TWFAACAV200D
       SET STTC_COL1 = #{sttcCol1}
          ,STTC_COL2 = #{sttcCol2}
          ,STTC_COL3 = #{sttcCol3}
          ,STTC_COL4 = #{sttcCol4}
          ,STTC_COL5 = #{sttcCol5}
          ,STTC_COL6 = #{sttcCol6}
          ,STTC_COL7 = #{sttcCol7}
          ,STTC_COL8 = #{sttcCol8}
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}
       AND STTC_DATA_SNO = #{sttcDataSno}
       AND ROW_KND_CD = #{rowKndCd}

/* kr.go.bai.eip.faa.cav.dao.FAACAV021QMapper.updateMasterDtmDy */

    UPDATE TWFAACAV200M
       SET STTC_DTM_DY = SYSDATE
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}
