mapper.select:
/* kr.go.bai.eip.faa.cav.dao.FAACAV025QMapper.selectLoadSttcList */
WITH CODE_DATA AS ( SELECT CD , CD_NM FROM TBDCMACM013C D2 WHERE D2.CMM_CD_DVSN_CD = '1000946' AND D2.CD <> '000000000' UNION SELECT 'Q' AS CD, '미분류' AS CD_NM FROM DUAL UNION SELECT '99' AS CD, '년 합계' AS CD_NM FROM DUAL ) ,DATA AS ( SELECT NVL(SUBSTR(T1.TG_ORG_CAT_CD, '0', '1'),'Q') AS TG_ORG_CAT_CD ,T1.ACP_RUT_CD ,T1.ACP_DH FROM TBCSPDCP101M T1 WHERE T1.REQ_DVSN_CD = '1' AND TO_CHAR(T1.ACP_DH,'YYYYMMDD') BETWEEN TO_CHAR(ADD_MONTHS(#{proStadDt}, -1), 'YYYY')||'0101' AND TO_CHAR(LAST_DAY(ADD_MONTHS(#{proStadDt}, -1)),'YYYYMMDD') ) ,PRE_DATA AS ( SELECT NVL(SUBSTR(T1.TG_ORG_CAT_CD, '0', '1'),'Q') AS TG_ORG_CAT_CD ,T1.ACP_RUT_CD ,T1.ACP_DH FROM TBCSPDCP101M T1 WHERE T1.REQ_DVSN_CD = '1' AND TO_CHAR(T1.ACP_DH,'YYYYMMDD') BETWEEN TO_CHAR(ADD_MONTHS(#{proStadDt}, -1), 'YYYY')||'0101' AND TO_CHAR(ADD_MONTHS(#{proStadDt}, -1), 'YYYY')||'1231' ) SELECT AUD_YR ,STTC_TIT1 ,ROW_KND_CD ,VIEW_YN ,STTC_COL1 ,STTC_COL2 ,STTC_COL3 ,STTC_COL4 ,STTC_COL5 ,STTC_COL6 ,STTC_COL7 ,STTC_COL8 ,STTC_COL9 ,STTC_COL10 FROM ( SELECT TO_CHAR(SUBSTR(ADD_MONTHS(#{proStadDt}, -1),0,4)) AS AUD_YR , CASE WHEN T2.CD = '99' THEN TO_CHAR(SUBSTR(ADD_MONTHS(#{proStadDt}, -1),0,4)) || T2.CD_NM ELSE T2.CD_NM END AS STTC_TIT1 , T2.CD AS ROW_KND_CD , 'Y' AS VIEW_YN , NVL(T1.CNT,0) AS STTC_COL1 , CASE WHEN NVL(T1.CNTALL,0) = 0 THEN 0 ELSE ROUND((NVL(T1.CNT,0)/T1.CNTALL) *100, 2) END AS STTC_COL2 , NVL(T1.CNT3,0) AS STTC_COL3 , NVL(T1.CNT4,0) AS STTC_COL4 , NVL(T1.CNT5,0) AS STTC_COL5 , NVL(T1.CNT6,0) AS STTC_COL6 , NVL(T1.CNT7,0) AS STTC_COL7 , NVL(T1.CNT8,0) AS STTC_COL8 , NVL(T1.CNT9,0) AS STTC_COL9 , NVL(T1.CNT10,0) AS STTC_COL10 FROM ( SELECT NVL(T1.TG_ORG_CAT_CD,'99') AS TG_ORG_CAT_CD ,COUNT(1) AS CNT ,(SELECT COUNT(1) FROM DATA) AS CNTALL ,SUM(CASE WHEN T1.ACP_RUT_CD = '7' THEN 1 ELSE 0 END) CNT3 ,SUM(CASE WHEN T1.ACP_RUT_CD = '1' THEN 1 ELSE 0 END) CNT4 ,SUM(CASE WHEN T1.ACP_RUT_CD = '8' THEN 1 ELSE 0 END) CNT5 ,SUM(CASE WHEN T1.ACP_RUT_CD = '2' THEN 1 ELSE 0 END) CNT6 ,SUM(CASE WHEN T1.ACP_RUT_CD = '5' THEN 1 ELSE 0 END) CNT7 ,SUM(CASE WHEN T1.ACP_RUT_CD = '4' THEN 1 ELSE 0 END) CNT8 ,SUM(CASE WHEN T1.ACP_RUT_CD = '3' THEN 1 ELSE 0 END) CNT9 ,SUM(CASE WHEN T1.ACP_RUT_CD = '9' THEN 1 ELSE 0 END) CNT10 FROM DATA T1 GROUP BY ROLLUP(T1.TG_ORG_CAT_CD) ) T1, CODE_DATA T2 WHERE T1.TG_ORG_CAT_CD(+) = T2.CD
/* ORDER BY CASE WHEN T2.CD = '99' THEN 1 WHEN T2.CD = '가' THEN 2 WHEN T2.CD = '나' THEN 8 WHEN T2.CD = '다' THEN 4 WHEN T2.CD = '라' THEN 3 WHEN T2.CD = '마' THEN 5 WHEN T2.CD = '바' THEN 6 WHEN T2.CD = '사' THEN 10 WHEN T2.CD = '아' THEN 9 WHEN T2.CD = '자' THEN 7 WHEN T2.CD = '차' THEN 11 WHEN T2.CD = '카' THEN 12 WHEN T2.CD = '타' THEN 13 WHEN T2.CD = '하' THEN 14 END*/
) UNION ALL
SELECT T2.AUD_YR ,STTC_TIT1 ,ROW_KND_CD ,'N' AS VIEW_YN ,STTC_COL1 ,STTC_COL2 ,STTC_COL3 ,STTC_COL4 ,STTC_COL5 ,STTC_COL6 ,STTC_COL7 ,STTC_COL8 ,STTC_COL9 ,STTC_COL10 FROM TWFAACAV200M T1 INNER JOIN TWFAACAV200D T2 ON ( T1.STTC_TYP_CD = T2.STTC_TYP_CD AND T1.STTC_CLSF_CD = T2.STTC_CLSF_CD AND T1.STTC_TYP_SEQ = T2.STTC_TYP_SEQ ) WHERE T1.STTC_CLSF_CD = '025' AND T2.STTC_TYP_SEQ = (SELECT MAX(T3.STTC_TYP_SEQ) FROM TWFAACAV200M T3 WHERE T3.STTC_CLSF_CD = '025' AND T3.STTC_DTM_DY IS NOT NULL) AND T2.AUD_YR BETWEEN SUBSTR(ADD_MONTHS(#{proStadDt}, -13),0,4) AND SUBSTR(ADD_MONTHS(#{proStadDt},-13),0,4) ORDER BY VIEW_YN DESC, ROW_KND_CD ASC

/* kr.go.bai.eip.aep.arp.dao.FAACAV025QMapper.selectAffComboList */ SELECT STTC_TYP_SEQ AS CD, STTC_YR || '년 ' || STTC_STR_MON || '월' || DECODE(STTC_END_MON, '01', '', ' ~ ' || STTC_END_MON || '월') AS CD_NM FROM TWFAACAV200M WHERE STTC_TYP_CD = '10' AND STTC_CLSF_CD = '025'
AND STTC_DTM_DY IS NOT NULL
ORDER BY STTC_TYP_SEQ DESC

mapper.insert:
/* kr.go.bai.eip.faa.cav.dao.FAACAV025QMapper.insertDetail */

    INSERT INTO TWFAACAV200D (
         STTC_TYP_CD
        ,STTC_CLSF_CD
        ,STTC_TYP_SEQ
        ,STTC_DATA_SNO
        ,STTC_TIT1
        ,STTC_COL1
        ,STTC_COL2
        ,STTC_COL3
        ,STTC_COL4
        ,STTC_COL5
        ,STTC_COL6
        ,STTC_COL7
        ,STTC_COL8
        ,STTC_COL9
        ,STTC_COL10
        ,AUD_YR
        ,ROW_KND_CD
        ,FRT_USR_ID
        ,FNL_USR_ID
        ,FNL_DEAL_DPT_CD
        ,FNL_MNU_ID
        ,FRT_REGI_DH
        ,FNL_MDF_DH )
    VALUES (
         '10'
        ,'025'
        ,( SELECT NVL(MAX(STTC_TYP_SEQ), 0)
             FROM TWFAACAV200M
            WHERE STTC_TYP_CD = '10'
              AND STTC_CLSF_CD = '025' )
        ,( SELECT NVL(MAX(STTC_DATA_SNO), 0) + 1
             FROM TWFAACAV200D
            WHERE STTC_TYP_CD = '10'
              AND STTC_CLSF_CD = '025' 
              AND STTC_TYP_SEQ = ( SELECT NVL(MAX(STTC_TYP_SEQ), 0)
                                     FROM TWFAACAV200M
                                    WHERE STTC_TYP_CD = '10'
                                      AND STTC_CLSF_CD = '025' ))
        ,#{sttcTit1}
        ,#{sttcCol1}
        ,#{sttcCol2}
        ,#{sttcCol3}
        ,#{sttcCol4}
        ,#{sttcCol5}
        ,#{sttcCol6}
        ,#{sttcCol7}
        ,#{sttcCol8}
        ,#{sttcCol9}
        ,#{sttcCol10}
        ,#{audYr}
        ,#{rowKndCd}
        ,#{usrId}
        ,#{usrId}
        ,#{dptCd}
        ,#{mnuId}
        ,SYSDATE
        ,SYSDATE )

/* kr.go.bai.eip.faa.cav.dao.FAACAV025QMapper.updateMaster */

    UPDATE TWFAACAV200M
       SET RMK = #{rmk}
          ,ADM_OPIN = #{admOpin}
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}

/* kr.go.bai.eip.faa.cav.dao.FAACAV025QMapper.updateDetail */

    UPDATE TWFAACAV200D
       SET STTC_COL1 = #{sttcCol1}
          ,STTC_COL2 = #{sttcCol2}
          ,STTC_COL3 = #{sttcCol3}
          ,STTC_COL4 = #{sttcCol4}
          ,STTC_COL5 = #{sttcCol5}
          ,STTC_COL6 = #{sttcCol6}
          ,STTC_COL7 = #{sttcCol7}
          ,STTC_COL8 = #{sttcCol8}
          ,STTC_COL9 = #{sttcCol9}
          ,STTC_COL10 = #{sttcCol10}
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}
       AND STTC_DATA_SNO = #{sttcDataSno}

/* kr.go.bai.eip.faa.cav.dao.FAACAV025QMapper.updateMasterDtmDy */

    UPDATE TWFAACAV200M
       SET STTC_DTM_DY = SYSDATE
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}
