mapper.select:
/* kr.go.bai.eip.faa.cav.dao.FAACAV029QMapper.selectLoadSttcList */
SELECT TO_CHAR(SUBSTR(ADD_MONTHS(#{proStadDt}, -1),0,4)) AS AUD_YR , '0' AS ROW_KND_CD , 'Y' AS VIEW_YN , TO_CHAR(SUBSTR(ADD_MONTHS(#{proStadDt}, -1),0,4)) || '년 합계' AS STTC_TIT1 ,'0' AS STTC_COL1 ,'0' AS STTC_COL2 ,'0' AS STTC_COL3 ,'0' AS STTC_COL4 ,'0' AS STTC_COL5 ,'0' AS STTC_COL6 ,'0' AS STTC_COL7 FROM DUAL UNION ALL SELECT TO_CHAR(SUBSTR(ADD_MONTHS(#{proStadDt}, -1),0,4)) AS AUD_YR , '1' AS ROW_KND_CD , 'Y' AS VIEW_YN ,'법령 제정·개폐' AS STTC_TIT1 ,'0' AS STTC_COL1 ,'0' AS STTC_COL2 ,'0' AS STTC_COL3 ,'0' AS STTC_COL4 ,'0' AS STTC_COL5 ,'0' AS STTC_COL6 ,'0' AS STTC_COL7 FROM DUAL UNION ALL SELECT TO_CHAR(SUBSTR(ADD_MONTHS(#{proStadDt}, -1),0,4)) AS AUD_YR , '2' AS ROW_KND_CD , 'Y' AS VIEW_YN ,'법령 해석 질의' AS STTC_TIT1 ,'0' AS STTC_COL1 ,'0' AS STTC_COL2 ,'0' AS STTC_COL3 ,'0' AS STTC_COL4 ,'0' AS STTC_COL5 ,'0' AS STTC_COL6 ,'0' AS STTC_COL7 FROM DUAL
UNION ALL SELECT T2.AUD_YR ,ROW_KND_CD ,CASE WHEN SUBSTR(ADD_MONTHS(#{proStadDt}, -1),6,2) = '01' AND T2.AUD_YR = (SELECT TO_CHAR(MAX(T2.AUD_YR)-1) FROM TWFAACAV200D T2 WHERE T2.STTC_CLSF_CD = '029' AND T2.STTC_TYP_SEQ = (SELECT MAX(T3.STTC_TYP_SEQ) FROM TWFAACAV200M T3 WHERE T3.STTC_CLSF_CD = '029' AND T3.STTC_DTM_DY IS NOT NULL)) THEN 'N' WHEN T1.STTC_YR - 2 > T2.AUD_YR THEN 'N' ELSE 'Y' END VIEW_YN ,STTC_TIT1 ,TO_CHAR(NVL(STTC_COL1,0)) ,TO_CHAR(NVL(STTC_COL2,0)) ,TO_CHAR(NVL(STTC_COL3,0)) ,TO_CHAR(NVL(STTC_COL4,0)) ,TO_CHAR(NVL(STTC_COL5,0)) ,TO_CHAR(NVL(STTC_COL6,0)) ,TO_CHAR(NVL(STTC_COL7,0)) FROM TWFAACAV200M T1 INNER JOIN TWFAACAV200D T2 ON ( T1.STTC_TYP_CD = T2.STTC_TYP_CD AND T1.STTC_CLSF_CD = T2.STTC_CLSF_CD AND T1.STTC_TYP_SEQ = T2.STTC_TYP_SEQ ) WHERE T1.STTC_CLSF_CD = '029' AND T2.STTC_TYP_SEQ = (SELECT MAX(T3.STTC_TYP_SEQ) FROM TWFAACAV200M T3 WHERE T3.STTC_CLSF_CD = '029' AND T3.STTC_DTM_DY IS NOT NULL) AND T2.AUD_YR BETWEEN SUBSTR(ADD_MONTHS(#{proStadDt}, -25),0,4) AND SUBSTR(ADD_MONTHS(#{proStadDt},-13),0,4)

/* kr.go.bai.eip.aep.arp.dao.FAACAV029QMapper.selectAffComboList */ SELECT STTC_TYP_SEQ AS CD, STTC_YR || '년 ' || STTC_STR_MON || '월' || DECODE(STTC_END_MON, '01', '', ' ~ ' || STTC_END_MON || '월') AS CD_NM FROM TWFAACAV200M WHERE STTC_TYP_CD = '10' AND STTC_CLSF_CD = '029'
AND STTC_DTM_DY IS NOT NULL
ORDER BY STTC_TYP_SEQ DESC

mapper.insert:
/* kr.go.bai.eip.faa.cav.dao.FAACAV029QMapper.updateMaster */

    UPDATE TWFAACAV200M
       SET RMK = #{rmk}
          ,ADM_OPIN = #{admOpin}
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}

/* kr.go.bai.eip.faa.cav.dao.FAACAV029QMapper.updateDetail */

    UPDATE TWFAACAV200D
       SET STTC_COL1 = #{sttcCol1}
          ,STTC_COL2 = #{sttcCol2}
          ,STTC_COL3 = #{sttcCol3}
          ,STTC_COL4 = #{sttcCol4}
          ,STTC_COL5 = #{sttcCol5}
          ,STTC_COL6 = #{sttcCol6}
          ,STTC_COL7 = #{sttcCol7}               
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}
       AND STTC_DATA_SNO = #{sttcDataSno}

/* kr.go.bai.eip.faa.cav.dao.FAACAV029QMapper.updateMasterDtmDy */

    UPDATE TWFAACAV200M
       SET STTC_DTM_DY = SYSDATE
          ,FNL_USR_ID = #{usrId}
          ,FNL_DEAL_DPT_CD = #{dptCd}
          ,FNL_MDF_DH = SYSDATE
     WHERE STTC_TYP_CD = #{sttcTypCd}
       AND STTC_CLSF_CD = #{sttcClsfCd}
       AND STTC_TYP_SEQ = #{sttcTypSeq}
