mapper.select:
/* kr.go.bai.eip.fdm.adm.dao.FDMADM005EMapper.selectTskStepList*/ SELECT CMM_CD_DVSN_CD, CD, CD_NM FROM TBDCMACM013C WHERE 1 = 1 AND CD <> '000000000' AND CMM_CD_DVSN_CD = #{tskKndCdDvsnCd} AND USR_DFN_TXT_1 = #{cmmCdDvsnCd}
AND USR_DFN_TXT_2 = #{tskKndCd}
ORDER BY CMM_CD_DVSN_CD, CD, CD_NM

mapper.delete:
/* kr.go.bai.eip.fdm.adm.dao.FDMADM005EMapper.delTskDraftDocData*/
 DELETE FROM TBFDMADM005M			   
 WHERE 1=1 
 	AND TSK_KND_CD = #{tskKndCd}
 	AND TSK_STEP_CD = #{tskStepCd}
 	AND TSK_DOC_CD = #{tskDocCd}
 	AND META_INF_SNO = #{metaInfSno};

/* kr.go.bai.eip.fdm.adm.dao.FDMADM005EMapper.delTskDocMetaInfData*/
 DELETE FROM TBFDMADM004M			   
 WHERE 1=1 
 	AND TSK_KND_CD = #{tskKndCd}
 	AND TSK_STEP_CD = #{tskStepCd}
 	AND TSK_DOC_CD = #{tskDocCd}
 	AND META_INF_SNO = #{metaInfSno};
