sqlMap.select:
SELECT AGGR.* FROM (SELECT TER.BZT_YR, TER.BZT_SNO, TER.PCT_CNB, /*전산번호*/ TER.PCT_NM, /*성명*/ F_CODE_NM('1000173',TER.PCT_RANK_CD) AS POS_CLASS_NM, /*직급*/ F_DPT_INFO(BL.BLT_BRDV_CD,'4') DPT_NM, /*소속*/ TE.IN_EXP, /*여비*/ TE.BZT_DCN, /*일수*/ NVL(TFD.RL_FR_EXS, 0) + NVL(TE.CPRG_FR_EXS,0) RL_FR_EXS, /*철도임 + 수도권운임비*/ NVL(TFD.RL_FR_EXS, 0) + TE.IN_EXP + NVL(TE.CPRG_FR_EXS,0) AS SUM_ALL, /*지급액*/ TE.BZT_STR_DT, /*출장기간FROM*/ TE.BZT_END_DT, /*출장기간TO*/ '' RECIPIENT /*영수인*/ FROM TBBADDED010L TER, TBBADDED002M SITU, (SELECT BZT_YR, BZT_SNO, PCT_CNB, SUM( CASE WHEN TE0.CALC_YN='Y' THEN TE0.FDEX + TE0.DT_EXS + TE0.LDC ELSE 0 END) - SUM( CASE WHEN TE0.CALC_YN='N' THEN TE0.FDEX + TE0.DT_EXS + TE0.LDC ELSE 0 END) AS IN_EXP,
/* 수도권운임 추가 - 2015.05.06 yyh*/ SUM( CASE WHEN TE0.CALC_YN='Y' THEN NVL(TE0.CPRG_FR_EXS,0) ELSE 0 END) - SUM( CASE WHEN TE0.CALC_YN='N' THEN NVL(TE0.CPRG_FR_EXS,0) ELSE 0 END) AS CPRG_FR_EXS,
SUM( CASE WHEN TE0.CALC_YN='Y' THEN TE0.BZT_DCN ELSE 0 END) - SUM( CASE WHEN TE0.CALC_YN='N' THEN TE0.BZT_DCN ELSE 0 END) AS BZT_DCN, MIN(BZT_STR_DT) AS BZT_STR_DT, MAX(BZT_END_DT) AS BZT_END_DT FROM TBBADDED011L TE0 WHERE BZT_YR = #strBztYr# AND BZT_SNO = #strBztSno# GROUP BY BZT_YR, BZT_SNO, PCT_CNB) TE, (SELECT BZT_YR, BZT_SNO, PCT_CNB, SUM( CASE WHEN CALC_YN='Y' THEN RL_FR_EXS ELSE 0 END) - SUM( CASE WHEN CALC_YN='N' THEN RL_FR_EXS ELSE 0 END) AS RL_FR_EXS FROM TBBADDED017L WHERE BZT_YR = #strBztYr# AND BZT_SNO = #strBztSno# GROUP BY BZT_YR, BZT_SNO, PCT_CNB ) TFD, (SELECT BZT_YR, BZT_SNO, PCT_CNB, MAX(BLT_BRDV_CD) AS BLT_BRDV_CD FROM TBBADDED005L WHERE BZT_YR = #strBztYr# AND BZT_SNO = #strBztSno# GROUP BY BZT_YR, BZT_SNO, PCT_CNB) BL WHERE TER.BZT_YR = #strBztYr# AND TER.BZT_SNO = #strBztSno# AND TE.BZT_YR = TER.BZT_YR AND TE.BZT_SNO = TER.BZT_SNO AND TE.PCT_CNB = TER.PCT_CNB AND TFD.BZT_YR(+) = TER.BZT_YR AND TFD.BZT_SNO(+) = TER.BZT_SNO AND TFD.PCT_CNB(+) = TER.PCT_CNB AND SITU.BZT_YR = TER.BZT_YR AND SITU.BZT_SNO = TER.BZT_SNO AND BL.BZT_YR(+) = TER.BZT_YR AND BL.BZT_SNO(+) = TER.BZT_SNO AND BL.PCT_CNB(+) = TER.PCT_CNB) AGGR WHERE AGGR.SUM_ALL != 0

SELECT AGGR.* FROM (SELECT TER.BZT_YR, TER.BZT_SNO, TER.PCT_CNB, /*전산번호*/ TER.PCT_NM, /*성명*/ TER.PCT_RANK_CD , F_CODE_NM('1000173',TER.PCT_RANK_CD) AS POS_CLASS_NM, /*직급*/ F_DPT_INFO(TER.BLT_BRDV_CD,'4') DPT_NM, /*소속*/ TER.STF_DVSN_CD, TER.DEP_RGN_CD, NVL(BEF.SPM_XPN,0) - NVL(AFT.SPM_XPN,0) AS SPM_XPN, /*보충경비*/ NVL(BEF.BZT_DCN,0) - NVL(AFT.BZT_DCN,0) AS BZT_DCN, /*일수*/ BEF.BZT_STR_DT, /*출장기간FROM*/ BEF.BZT_END_DT, /*출장기간TO*/ '' RECIPIENT, /*영수인*/ NVL(BEF.LEAD_EXS,0) - NVL(AFT.LEAD_EXS,0) AS LEAD_EXS, /*지휘비*/ NVL(BEF.AUD_ACTV_RCV_EXS,0) - NVL(AFT.AUD_ACTV_RCV_EXS,0) AS AUD_ACTV_RCV_EXS, /*감사활동 수용비*/ NVL(BEF.SPM_XPN,0) - NVL(AFT.SPM_XPN,0) + NVL(BEF.LEAD_EXS,0) - NVL(AFT.LEAD_EXS,0) + NVL(BEF.AUD_ACTV_RCV_EXS,0) - NVL(AFT.AUD_ACTV_RCV_EXS,0) AS SUM_ALL /*지급액*/ FROM TBBADDED010L TER, TBBADDED002M SITU, ( SELECT D1.BZT_YR , D1.BZT_SNO , D1.PCT_CNB , SUM(NVL(TE.SPM_XPN,0)) AS SPM_XPN , SUM(NVL(TE.BZT_DCN,0)) AS BZT_DCN , MIN(TE.BZT_STR_DT) AS BZT_STR_DT , MAX(TE.BZT_END_DT) AS BZT_END_DT , CASE WHEN D1.PCT_CNB = F_LEAD_EXS_RCV_EXS_RCIT_CNB(D1.BZT_YR, D1.BZT_SNO) THEN MAX(NVL(D5.LEAD_EXS,0)) ELSE 0 END AS LEAD_EXS , CASE WHEN D1.PCT_CNB = F_LEAD_EXS_RCV_EXS_RCIT_CNB(D1.BZT_YR, D1.BZT_SNO) THEN MAX(NVL(D5.AUD_ACTV_RCV_EXS,0)) ELSE 0 END AUD_ACTV_RCV_EXS FROM (SELECT BZT_YR , BZT_SNO , PCT_CNB , MAX(BZT_AJT_SNO) MAX_SNO , MAX(AJT_CHG_RSN) AJT_CHG_RSN FROM TBBADDED019H WHERE BZT_YR = #strBztYr# AND BZT_SNO = #strBztSno# AND NVL(AJT_YN, 'N') = 'N' GROUP BY BZT_YR , BZT_SNO , PCT_CNB) D1 , ( SELECT TE.* , TFD.RL_FR_EXS FROM TBBADDED019H TE , (SELECT BZT_YR , BZT_SNO , PCT_CNB , BZT_EXS_SNO, BZT_AJT_SNO, SUM( RL_FR_EXS ) RL_FR_EXS FROM TBBADDED020H GROUP BY BZT_YR , BZT_SNO , PCT_CNB , BZT_EXS_SNO, BZT_AJT_SNO ) TFD WHERE 1=1 AND TE.BZT_YR = TFD.BZT_YR(+) AND TE.BZT_SNO = TFD.BZT_SNO(+) AND TE.PCT_CNB = TFD.PCT_CNB(+) AND TE.BZT_EXS_SNO = TFD.BZT_EXS_SNO(+) AND TE.BZT_AJT_SNO = TFD.BZT_AJT_SNO(+)
) TE , ( SELECT D1.BZT_YR , D1.BZT_SNO , D1.BZT_AJT_SNO , SUM(NVL(D1.LEAD_EXS ,0)) AS LEAD_EXS , SUM(NVL(D1.AUD_ACTV_RCV_EXS ,0)) AUD_ACTV_RCV_EXS FROM TBBADDED019H D1 WHERE 1=1 AND D1.BZT_YR = #strBztYr# AND D1.BZT_SNO = #strBztSno# GROUP BY D1.BZT_YR , D1.BZT_SNO , D1.BZT_AJT_SNO ) D5 WHERE 1=1 AND D1.BZT_YR = TE.BZT_YR(+) AND D1.BZT_SNO = TE.BZT_SNO(+) AND D1.PCT_CNB = TE.PCT_CNB(+) AND D1.MAX_SNO = TE.BZT_AJT_SNO(+) AND D1.BZT_YR = D5.BZT_YR(+) AND D1.BZT_SNO = D5.BZT_SNO(+) AND D1.MAX_SNO = D5.BZT_AJT_SNO(+) AND D1.BZT_YR = #strBztYr# AND D1.BZT_SNO = #strBztSno# GROUP BY D1.BZT_YR , D1.BZT_SNO , D1.PCT_CNB ) BEF, ( SELECT D1.BZT_YR , D1.BZT_SNO , D1.PCT_CNB , MIN(TE.BZT_STR_DT) AS BZT_STR_DT , MAX(TE.BZT_END_DT) AS BZT_END_DT , SUM(NVL(TE.SPM_XPN,0)) AS SPM_XPN , SUM(NVL(TE.BZT_DCN,0)) AS BZT_DCN , MIN(TE.BZT_STR_DT) AS BZT_STR_DT , MAX(TE.BZT_END_DT) AS BZT_END_DT , CASE WHEN D1.PCT_CNB = F_LEAD_EXS_RCV_EXS_RCIT_CNB(D1.BZT_YR, D1.BZT_SNO) THEN MAX(NVL(D5.LEAD_EXS,0)) ELSE 0 END AS LEAD_EXS , CASE WHEN D1.PCT_CNB = F_LEAD_EXS_RCV_EXS_RCIT_CNB(D1.BZT_YR, D1.BZT_SNO) THEN MAX(NVL(D5.AUD_ACTV_RCV_EXS,0)) ELSE 0 END AUD_ACTV_RCV_EXS FROM (SELECT BZT_YR , BZT_SNO , PCT_CNB , MAX(BZT_AJT_SNO) MAX_SNO , MAX(AJT_CHG_RSN) AJT_CHG_RSN FROM TBBADDED019H WHERE BZT_YR = #strBztYr# AND BZT_SNO = #strBztSno# AND NVL(AJT_YN, 'N') = 'Y' GROUP BY BZT_YR , BZT_SNO , PCT_CNB) D1 , ( SELECT TE.* , TFD.RL_FR_EXS FROM TBBADDED019H TE , (SELECT BZT_YR , BZT_SNO , PCT_CNB , BZT_EXS_SNO, BZT_AJT_SNO, SUM( RL_FR_EXS ) RL_FR_EXS FROM TBBADDED020H GROUP BY BZT_YR , BZT_SNO , PCT_CNB , BZT_EXS_SNO, BZT_AJT_SNO ) TFD WHERE 1=1 AND TE.BZT_YR = TFD.BZT_YR(+) AND TE.BZT_SNO = TFD.BZT_SNO(+) AND TE.PCT_CNB = TFD.PCT_CNB(+) AND TE.BZT_EXS_SNO = TFD.BZT_EXS_SNO(+) AND TE.BZT_AJT_SNO = TFD.BZT_AJT_SNO(+)
) TE , ( SELECT D1.BZT_YR , D1.BZT_SNO , D1.BZT_AJT_SNO , SUM(NVL(D1.LEAD_EXS ,0)) AS LEAD_EXS , SUM(NVL(D1.AUD_ACTV_RCV_EXS ,0)) AUD_ACTV_RCV_EXS FROM TBBADDED019H D1 WHERE 1=1 AND D1.BZT_YR = #strBztYr# AND D1.BZT_SNO = #strBztSno# GROUP BY D1.BZT_YR , D1.BZT_SNO , D1.BZT_AJT_SNO ) D5 WHERE 1=1 AND D1.BZT_YR = TE.BZT_YR(+) AND D1.BZT_SNO = TE.BZT_SNO(+) AND D1.PCT_CNB = TE.PCT_CNB(+) AND D1.MAX_SNO = TE.BZT_AJT_SNO(+) AND D1.BZT_YR = D5.BZT_YR(+) AND D1.BZT_SNO = D5.BZT_SNO(+) AND D1.MAX_SNO = D5.BZT_AJT_SNO(+) AND D1.BZT_YR = #strBztYr# AND D1.BZT_SNO = #strBztSno# GROUP BY D1.BZT_YR , D1.BZT_SNO , D1.PCT_CNB ) AFT WHERE 1=1 AND SITU.BZT_YR = TER.BZT_YR AND SITU.BZT_SNO = TER.BZT_SNO AND TER.BZT_YR = BEF.BZT_YR(+) AND TER.BZT_SNO = BEF.BZT_SNO(+) AND TER.PCT_CNB = BEF.PCT_CNB(+) AND TER.BZT_YR = AFT.BZT_YR(+) AND TER.BZT_SNO = AFT.BZT_SNO(+) AND TER.PCT_CNB = AFT.PCT_CNB(+) AND TER.BZT_YR = #strBztYr# AND TER.BZT_SNO = #strBztSno#
) AGGR WHERE AGGR.SUM_ALL !=0
