SELECT AUD_YR , AUD_NO , AUD_SBJ_NM , SPV_BRDV_CD , SPV_BRDV_NM , RSV_INSPC_LUNH_DT , HNDL_DDLN_DT , FLD_AUD_STR_DT , FLD_AUD_END_DT , FLD_AUD_EXEC_DCN , RTN_DT , RPT_DDLN_DT , CHF_CMTR_CNB , CHF_CMTR_NM , SBCN_DT , AUD_EXEC_HNDL_STA_CD , FLD_EXTN_STR_DT , FLD_EXTN_END_DT , DTL_AUD_NO , AUD_KND_CD , AUD_KND_NM , EXTN_PRD_COFM_DT , GNR_HNDL_PLAN_YN , HNDL_SITU_HIS , STT_RMK , MDF_YN , INDC_NOC , DELAY_CNT , HNDL_STATUS , RTN_RPT_DT , DIFF_REPORT , CASE WHEN DIFF_REPORT = '-' THEN '-' WHEN DIFF_REPORT > 0 THEN '경고' ELSE '' END AS DIFF_REPORT_STATUS , BRDV_HNDL_DDLN_DT , AUD_BRDV_APV_DT , DIFF_BRDV , CASE WHEN DIFF_BRDV = '-' THEN '-' WHEN DIFF_BRDV > 0 THEN '경고' ELSE '' END AS DIFF_BRDV_STATUS , MDT_CHGR_APV_DDLN_DT , MDT_OFER_APV_DT , DIFF_MDT_CHGR , CASE WHEN DIFF_MDT_CHGR = '-' THEN '-' WHEN DIFF_MDT_CHGR > 0 THEN '경고' ELSE '' END AS DIFF_MDT_CHGR_STATUS , MDT_OFER_APV_DDLN_DT , MDT_DDG_APV_DT , DIFF_MDT_OFER , CASE WHEN DIFF_MDT_OFER = '-' THEN '-' WHEN DIFF_MDT_OFER > 0 THEN '경고' ELSE '' END AS DIFF_MDT_OFER_STATUS , RVW_RM_APV_DDLN_DT , JA_JDG_SSEC_APV_DT , DIFF_RVW_RM , CASE WHEN DIFF_RVW_RM = '-' THEN '-' WHEN DIFF_RVW_RM > 0 THEN '경고' ELSE '' END AS DIFF_RVW_RM_STATUS , TOT_DHD_APV_DDLN_DT , OW_DHD_APV_DT , DIFF_TOT_DHD , CASE WHEN DIFF_TOT_DHD = '-' THEN '-' WHEN DIFF_TOT_DHD > 0 THEN '경고' ELSE '' END AS DIFF_TOT_DHD_STATUS , CHF_CMTR_APV_DDLN_DT , CHF_CMTR_APV_DT , DIFF_CHF_CMTR , CASE WHEN DIFF_CHF_CMTR = '-' THEN '-' WHEN DIFF_CHF_CMTR > 0 THEN '경고' ELSE '' END AS DIFF_CHF_CMTR_STATUS , ENF_APV_DDLN_DT , ENF_APV_DT , DIFF_ENF , CASE WHEN DIFF_ENF = '-' THEN '-' WHEN DIFF_ENF > 0 THEN '경고' ELSE '' END AS DIFF_ENF_STATUS , DIFF_HNDL , FLD_AUD_DT , AUD_YR_NO , DECODE(DIFF_REPORT,'-',0,DIFF_REPORT) +DECODE(DIFF_BRDV,'-',0,DIFF_REPORT) +DECODE(DIFF_MDT_CHGR,'-',0,DIFF_REPORT) +DECODE(DIFF_MDT_OFER,'-',0,DIFF_MDT_OFER) +DECODE(DIFF_TOT_DHD,'-',0,DIFF_TOT_DHD) +DECODE(DIFF_CHF_CMTR,'-',0,DIFF_CHF_CMTR) +DECODE(DIFF_ENF,'-',0,DIFF_ENF) AS SUM_DIFF FROM (
SELECT B.AUD_YR AS AUD_YR /*감사년도*/ , B.AUD_NO AS AUD_NO /*감사순번*/ , B.AUD_YR ||'-'|| B.AUD_NO AS AUD_YR_NO /*연번호*/ , B.AUD_YR ||'-'|| B.AUD_NO ||' ('|| F_CODE_NM('1000007',B.AUD_KND_CD) ||')' ||B.AUD_SBJ_NM AS AUD_SBJ_NM /*감사사항명*/ , B.SPV_BRDV_CD AS SPV_BRDV_CD /*주관부서코드*/ , F_DPT_INFO(B.SPV_BRDV_CD,'1') AS SPV_BRDV_NM /*주관부서명*/ , A.RSV_INSPC_LUNH_DT AS RSV_INSPC_LUNH_DT /*예비조사착수일*/ , A.HNDL_DDLN_DT AS HNDL_DDLN_DT /*처리기한일*/ , A.FLD_AUD_STR_DT AS FLD_AUD_STR_DT /*실지감사시작일*/ , A.FLD_AUD_END_DT AS FLD_AUD_END_DT /*실지감사 종료일*/ , A.FLD_AUD_EXEC_DCN AS FLD_AUD_EXEC_DCN /* 실시기간*/ , CASE WHEN A.FLD_AUD_STR_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.FLD_AUD_STR_DT),'YYYY-MM-DD')||'-'||TO_CHAR(TO_DATE(A.FLD_AUD_END_DT),'YYYY-MM-DD') ||'('||A.FLD_AUD_EXEC_DCN ||')' ELSE '' END AS FLD_AUD_DT , A.RTN_DT AS RTN_DT /*귀청일자*/ , A.RPT_DDLN_DT AS RPT_DDLN_DT /*보고기한일*/ , A.CHF_CMTR_CNB AS CHF_CMTR_CNB /*주심위원전산번호*/ , F_USR_INFO(A.CHF_CMTR_CNB,'2') AS CHF_CMTR_NM /*주심위원성명*/ , A.SBCN_DT AS SBCN_DT /*부의일*/ , A.AUD_EXEC_HNDL_STA_CD AS AUD_EXEC_HNDL_STA_CD /*감사실시처리상태코드*/ , FLD_EXTN_STR_DT AS FLD_EXTN_STR_DT /*실지연장시작일(AS-IS때는 없던 컬럼 TO-BE부터 관리)*/ , FLD_EXTN_END_DT AS FLD_EXTN_END_DT /*실지연장종료일(AS-IS때는 없던 컬럼 TO-BE부터 관리)*/ , SUBSTR(B.AUD_YR, 3, 2) || '-' || SUBSTR(B.AUD_TASK_CD,-1) ||'-'|| B.ORG_DVSN_CD || '-' || SUBSTR(B.AUD_KND_CD, 3, 1) ||B.SPV_BRDV_CD || '-' || SUBSTR(B.AUD_WAY_CD,-1)||'-' || B.AUD_NO ||'-' ||(SELECT MAX(C.AUD_STEP_SNO) FROM TBBADDED002M C WHERE B.AUD_YR = C.REL_AUD_YR AND B.AUD_NO = C.REL_AUD_NO) AS DTL_AUD_NO /*세부연번호*/ , B.AUD_KND_CD AS AUD_KND_CD /*감사종류코드*/ , F_CODE_NM('1000007',B.AUD_KND_CD) AS AUD_KND_NM /*감사종류명*/ , EXTN_PRD_COFM_DT AS EXTN_PRD_COFM_DT /*연장기간승인일*/ , GNR_HNDL_PLAN_YN AS GNR_HNDL_PLAN_YN /*종합처리안여부*/ , HNDL_SITU_HIS AS HNDL_SITU_HIS /*처리현황내역*/ , STT_RMK AS STT_RMK /*상황비고*/ , MDF_YN AS MDF_YN /*수정여부*/ , INDC_NOC AS INDC_NOC /*지적건수*/ , CASE WHEN A.ENF_APV_DT IS NOT NULL THEN '-' /* 시행결재일자가 있으면 지연일수 없음*/ WHEN A.OW_DHD_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(SYSDATE)-TO_DATE(A.ENF_APV_DDLN_DT)) WHEN A.JA_JDG_SSEC_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(SYSDATE)-TO_DATE(A.TOT_DHD_APV_DDLN_DT)) WHEN A.MDT_DDG_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(SYSDATE)-TO_DATE(A.RVW_RM_APV_DDLN_DT)) WHEN A.MDT_OFER_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(SYSDATE)-TO_DATE(A.MDT_OFER_APV_DDLN_DT)) WHEN A.AUD_BRDV_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(SYSDATE)-TO_DATE(A.MDT_CHGR_APV_DDLN_DT)) WHEN A.RTN_RPT_DT IS NOT NULL THEN TO_CHAR(TO_DATE(SYSDATE)-TO_DATE(A.BRDV_HNDL_DDLN_DT)) ELSE '-' END DELAY_CNT /*현단계지연일수*/
/*성과평가 지연일수 구하기
,CASE WHEN A.ENF_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.ENF_APV_DT) - TO_DATE(A.CHF_CMTR_APV_DT) - 0) 성과평가에서 시행의 기준일수는 0으로 셋팅
WHEN A.CHF_CMTR_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.CHF_CMTR_APV_DT) - TO_DATE(A.OW_DHD_APV_DT) - 0) 성과평가에서 주심위원의 기준일수는 0으로 셋팅
WHEN A.OW_DHD_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.OW_DHD_APV_DT) - TO_DATE(A.JA_JDG_SSEC_APV_DT) - F_DDLN_DCN(AUD.AUD_YR, AUD.AUD_NO,'08'))
WHEN A.JA_JDG_SSEC_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.JA_JDG_SSEC_APV_DT) - TO_DATE(A.MDT_DDG_APV_DT) - F_DDLN_DCN(AUD.AUD_YR, AUD.AUD_NO,'07'))
WHEN A.MDT_DDG_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.MDT_DDG_APV_DT) - TO_DATE(A.MDT_OFER_APV_DT) - F_DDLN_DCN(AUD.AUD_YR, AUD.AUD_NO,'06'))
WHEN A.MDT_OFER_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.MDT_OFER_APV_DT) - TO_DATE(A.AUD_BRDV_APV_DT) - F_DDLN_DCN(AUD.AUD_YR, AUD.AUD_NO,'05'))
WHEN A.AUD_BRDV_APV_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.AUD_BRDV_APV_DT) - TO_DATE(A.RTN_RPT_DT) - F_DDLN_DCN(AUD.AUD_YR, AUD.AUD_NO,'04'))
WHEN A.RTN_RPT_DT IS NOT NULL THEN TO_CHAR(TO_DATE(A.RTN_RPT_DT) - TO_DATE(A.FLD_AUD_END_DT) - F_DDLN_DCN(AUD.AUD_YR, AUD.AUD_NO,'03'))
ELSE NULL
END DELAY_CNT 현단계지연일수*/ , CASE WHEN A.AUD_EXEC_HNDL_STA_CD = '1' AND TO_DATE(A.RTN_DT) > TO_DATE(SYSDATE) THEN '감사중' WHEN (A.AUD_EXEC_HNDL_STA_CD = '1' AND TRIM(A.HNDL_DDLN_DT) IS NULL) OR A.AUD_EXEC_HNDL_STA_CD = '3' THEN '처리중' WHEN A.AUD_EXEC_HNDL_STA_CD = '1'AND NVL(TO_DATE(A.HNDL_DDLN_DT),'99991231') < TO_DATE(SYSDATE) THEN '처리기한경과' WHEN A.AUD_EXEC_HNDL_STA_CD = '3' THEN '보류' WHEN A.AUD_EXEC_HNDL_STA_CD = '9' THEN '종료' ELSE '처리중' END HNDL_STATUS /*처리상태*/ , RTN_RPT_DT /*귀청보고결재일*/ , CASE WHEN TRIM(RTN_RPT_DT) IS NOT NULL THEN /*귀청보고일이 있는 경우*/ CASE WHEN TRIM(RPT_DDLN_DT) IS NULL THEN '0' ELSE TO_CHAR(TO_DATE(RTN_RPT_DT) - TO_DATE(RPT_DDLN_DT)) END ELSE CASE WHEN TRIM(RPT_DDLN_DT) IS NULL THEN '0' ELSE TO_CHAR(TO_DATE(SYSDATE) - TO_DATE(RPT_DDLN_DT)) END END DIFF_REPORT /*귀청보고지연일수*/
, BRDV_HNDL_DDLN_DT /*감사국과기한일*/ , AUD_BRDV_APV_DT /*감사국과결재일*/ , CASE WHEN TRIM(RTN_RPT_DT) IS NOT NULL THEN /*귀청보고일이 있는 */ CASE WHEN TRIM(AUD_BRDV_APV_DT) IS NULL THEN TO_CHAR(TO_DATE(SYSDATE) - TO_DATE(BRDV_HNDL_DDLN_DT)) ELSE TO_CHAR(TO_DATE(AUD_BRDV_APV_DT) - TO_DATE(BRDV_HNDL_DDLN_DT)) END ELSE '-' END DIFF_BRDV /*감사국과지연일수*/ , MDT_CHGR_APV_DDLN_DT /*조정담당자기한일*/ , MDT_OFER_APV_DT /*조정담당자결재일 */ , CASE WHEN TRIM(AUD_BRDV_APV_DT) IS NOT NULL THEN /*감사국과결재일이 있는 경우*/ CASE WHEN TRIM(MDT_OFER_APV_DT) IS NULL THEN TO_CHAR(TO_DATE(SYSDATE) - TO_DATE(MDT_CHGR_APV_DDLN_DT)) ELSE TO_CHAR(TO_DATE(MDT_OFER_APV_DT) - TO_DATE(MDT_CHGR_APV_DDLN_DT)) END ELSE '-' END DIFF_MDT_CHGR /*조정담당자지연일수*/ , MDT_OFER_APV_DDLN_DT /*조정담당관기한일*/ , MDT_DDG_APV_DT /*조정담당관결재일 */ , CASE WHEN TRIM(MDT_OFER_APV_DT) IS NOT NULL /*조정담당자결재일이 있는 경우*/ THEN CASE WHEN TRIM(MDT_DDG_APV_DT) IS NULL THEN TO_CHAR(TO_DATE(SYSDATE) - TO_DATE(MDT_OFER_APV_DDLN_DT)) ELSE TO_CHAR(TO_DATE(MDT_DDG_APV_DT) - TO_DATE(MDT_OFER_APV_DDLN_DT)) END ELSE '-' END DIFF_MDT_OFER /*조정담당관지연일수 */
, RVW_RM_APV_DDLN_DT /*심의실장기한일*/ , JA_JDG_SSEC_APV_DT /*법무심사관결재일(심의실장) */ , CASE WHEN TRIM(MDT_DDG_APV_DT) IS NOT NULL THEN /*조정담당관결재일이 있는 경우*/ CASE WHEN TRIM(JA_JDG_SSEC_APV_DT) IS NULL THEN TO_CHAR(TO_DATE(SYSDATE) - TO_DATE(RVW_RM_APV_DDLN_DT)) ELSE TO_CHAR(TO_DATE(JA_JDG_SSEC_APV_DT) - TO_DATE(RVW_RM_APV_DDLN_DT)) END ELSE '-' END DIFF_RVW_RM /*심의실장지연일수*/
,TOT_DHD_APV_DDLN_DT /*총차장기한일*/ ,OW_DHD_APV_DT /*사무차장결재일*/ ,CASE WHEN TRIM(JA_JDG_SSEC_APV_DT) IS NOT NULL THEN /*법무심사관(심의실장)결재일이 있는 경우*/ CASE WHEN TRIM(OW_DHD_APV_DT) IS NULL THEN TO_CHAR(TO_DATE(SYSDATE) - TO_DATE(TOT_DHD_APV_DDLN_DT)) ELSE TO_CHAR(TO_DATE(OW_DHD_APV_DT) - TO_DATE(TOT_DHD_APV_DDLN_DT)) END ELSE '-' END DIFF_TOT_DHD /*총차장지연일수*/ ,CHF_CMTR_APV_DDLN_DT /*주심위원기한일*/ ,CHF_CMTR_APV_DT /*주심위원결재일*/ ,CASE WHEN TRIM(OW_DHD_APV_DT) IS NOT NULL THEN /*사무차장결재일이 있는경우*/ CASE WHEN TRIM(CHF_CMTR_APV_DT) IS NULL THEN TO_CHAR(TO_DATE(SYSDATE) - TO_DATE(CHF_CMTR_APV_DDLN_DT)) ELSE TO_CHAR(TO_DATE(CHF_CMTR_APV_DT) - TO_DATE(CHF_CMTR_APV_DDLN_DT)) END ELSE '-' END DIFF_CHF_CMTR /*주심위원지연일수*/ ,ENF_APV_DDLN_DT /*시행기한일*/ ,ENF_APV_DT /*시행결재일*/ ,CASE WHEN TRIM(CHF_CMTR_APV_DT) IS NOT NULL THEN /*주심위원결재일이 있는 경우*/ CASE WHEN TRIM(ENF_APV_DT) IS NULL THEN CASE WHEN TRIM(ENF_APV_DDLN_DT) IS NULL THEN '0' ELSE TO_CHAR(TO_DATE(SYSDATE) - TO_DATE(ENF_APV_DDLN_DT)) END ELSE CASE WHEN TRIM(ENF_APV_DDLN_DT) IS NULL THEN '0' ELSE TO_CHAR(TO_DATE(ENF_APV_DT) - TO_DATE(ENF_APV_DDLN_DT)) END END ELSE '-' END DIFF_ENF /*시행지연일수*/ ,CASE WHEN TRIM(ENF_APV_DT) IS NOT NULL THEN /*시행결재일이 있는 경우*/ TO_CHAR(TO_DATE(ENF_APV_DT) - TO_DATE(HNDL_DDLN_DT)) ELSE CASE WHEN TRIM(HNDL_DDLN_DT) IS NULL THEN '0' ELSE TO_CHAR(TO_DATE(SYSDATE) - TO_DATE(HNDL_DDLN_DT)) END END DIFF_HNDL /*처리기한지연일수*/ FROM TBBADDED003M A ,TBBADDED001M B WHERE A.AUD_YR(+) = B.AUD_YR AND A.AUD_NO(+) = B.AUD_NO AND B.AUD_YR =TO_CHAR(SYSDATE,'YYYY') /*고정셋팅(현재년도)*/ /*AND B.SPV_BRDV_CD IN (SELECT DPT_CD FROM TABLE(F_AUTH_DEPT('0001','dptAuth', ''))) 직급이 원장일 경우 'ALL' 그외는 'DEPTLW'로 셋팅*/
/*특정(종합분석) 클릭시*/ AND substr(B.AUD_KND_CD,-1) ='0'
/*재무감사 클릭시*/ AND substr(B.AUD_KND_CD,-1) ='1'
/*특정감사 클릭시*/ AND substr(B.AUD_KND_CD,-1) ='2'
/*성과감사 클릭시*/ AND substr(B.AUD_KND_CD,-1) ='3'
/*기관운영감사 클릭시*/ AND substr(B.AUD_KND_CD,-1) ='4'
/*확인출장 클릭시*/ AND substr(B.AUD_KND_CD,-1) ='5'
/*재무감사(결산) 클릭시*/ AND substr(B.AUD_KND_CD,-1) ='6'
/*민원/손망실등 클릭시*/ AND substr(B.AUD_KND_CD,-1) ='7'
/*기타 클릭시*/ AND substr(B.AUD_KND_CD,-1) ='8'
/*서면/모니터링 클릭시*/ AND substr(B.AUD_KND_CD,-1) ='9'
/*귀청보고결재건수 클릭시*/ AND trim(RTN_RPT_DT) is not null AND trim(AUD_BRDV_APV_DT) is null AND trim(MDT_OFER_APV_DT) is null and trim(MDT_DDG_APV_DT) is null and trim(JA_JDG_SSEC_APV_DT) is null and trim(OW_DHD_APV_DT) is null and trim(ENF_APV_DT) is null
/*감사국과결재건수 클릭시*/ AND trim(RTN_RPT_DT) is not null and trim(AUD_BRDV_APV_DT) is not null and trim(MDT_OFER_APV_DT) is null and trim(MDT_DDG_APV_DT) is null and trim(JA_JDG_SSEC_APV_DT) is null and trim(OW_DHD_APV_DT) is null and trim(ENF_APV_DT) is null
/*조정담당자결재건수 클릭시 */ AND trim(RTN_RPT_DT) is not null and trim(AUD_BRDV_APV_DT) is not null and trim(MDT_OFER_APV_DT) is not null and trim(MDT_DDG_APV_DT) is null and trim(JA_JDG_SSEC_APV_DT) is null and trim(OW_DHD_APV_DT) is null and trim(ENF_APV_DT) is null
/*조정담당관결재건수 클릭시*/ AND trim(RTN_RPT_DT) is not null and trim(AUD_BRDV_APV_DT) is not null and trim(MDT_OFER_APV_DT) is not null and trim(MDT_DDG_APV_DT) is not null and trim(JA_JDG_SSEC_APV_DT) is null and trim(OW_DHD_APV_DT) is null and trim(ENF_APV_DT) is null
/*심의실장결재건수 클릭시*/ AND trim(RTN_RPT_DT) is not null and trim(AUD_BRDV_APV_DT) is not null and trim(MDT_OFER_APV_DT) is not null and trim(MDT_DDG_APV_DT) is not null and trim(JA_JDG_SSEC_APV_DT) is not null and trim(OW_DHD_APV_DT) is null and trim(ENF_APV_DT) is null
/*총차장결재건수 클릭시*/ AND trim(RTN_RPT_DT) is not null and trim(AUD_BRDV_APV_DT) is not null and trim(MDT_OFER_APV_DT) is not null and trim(MDT_DDG_APV_DT) is not null and trim(JA_JDG_SSEC_APV_DT) is not null and trim(OW_DHD_APV_DT) is not null and trim(ENF_APV_DT) is null
/*시행결재건수 클릭시*/
AND trim(RTN_RPT_DT) is not null and trim(AUD_BRDV_APV_DT) is not null and trim(MDT_OFER_APV_DT) is not null and trim(MDT_DDG_APV_DT) is not null and trim(JA_JDG_SSEC_APV_DT) is not null and trim(OW_DHD_APV_DT) is not null and trim(ENF_APV_DT) is not null
) ORDER BY AUD_NO
